// donationRequestContext.js

export const donationRequestState = {
  requests: [],
  loading: false,
  error: null,
};

export const donationRequestReducer = (state, action) => {
  switch (action.type) {
    case "SET_LOADING":
      return { ...state, loading: true };
    case "SET_REQUESTS":
      return { ...state, loading: false, requests: action.payload };
    case "SET_ERROR":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};
