import React, { Fragment, createContext, useReducer } from "react";
import AdminLayout from "../layout";
import DonationRequestActions from "./DonationRequestActions";
import DonationRequestDetails from "./DonationRequestDetails";
import DonationRequestsList from "./DonationRequestsList";
import { donationRequestState, donationRequestReducer } from "./donationRequestContext"; // assuming this exists

export const DonationRequestContext = createContext();

const DonationRequestComponent = () => {
  return (
    <div className="grid grid-cols-1 space-y-4 p-4">
      <DonationRequestActions />
      <DonationRequestDetails />
      <DonationRequestsList />
    </div>
  );
};

const DonationRequests = () => {
  const [data, dispatch] = useReducer(donationRequestReducer, donationRequestState);

  return (
    <Fragment>
      <DonationRequestContext.Provider value={{ data, dispatch }}>
        <AdminLayout children={<DonationRequestComponent />} />
      </DonationRequestContext.Provider>
    </Fragment>
  );
};

export default DonationRequests;
