import axios from "axios";

const apiURL = process.env.REACT_APP_API_URL || "http://localhost:8000";

export const DashboardData = async () => {
  try {
    const res = await axios.post(`${apiURL}/api/customize/dashboard-data`);
    console.log("Dashboard API Response:", res.data); // Debug log
    return res.data;
  } catch (error) {
    console.error("Dashboard API Error:", error.response?.data || error.message);
    throw error; // Re-throw to handle in calling function
  }
};

export const getSliderImages = async () => {
  try {
    const res = await axios.get(`${apiURL}/api/customize/get-slide-image`);
    return res.data;
  } catch (error) {
    console.error("Get Slider Images Error:", error.response?.data || error.message);
    throw error;
  }
};

export const postUploadImage = async (formData) => {
  try {
    const res = await axios.post(
      `${apiURL}/api/customize/upload-slide-image`,
      formData,
      {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }
    );
    return res.data;
  } catch (error) {
    console.error("Upload Image Error:", error.response?.data || error.message);
    throw error;
  }
};

export const postDeleteImage = async (id) => {
  try {
    const res = await axios.post(`${apiURL}/api/customize/delete-slide-image`, {
      id,
    });
    return res.data;
  } catch (error) {
    console.error("Delete Image Error:", error.response?.data || error.message);
    throw error;
  }
};
